import React from 'react';



class Register extends React.Component{
constructor(props){
super(props);

this.state={
nameReg:'',
emailReg:'',
passwordReg:''
}


}

onRegisterName = (event)=>{

this.setState({nameReg: event.target.value});


}
onRegisterEmail = (event)=>{

this.setState({email: event.target.value});


}

onRegisterPassword = (event)=>{

this.setState({password: event.target.value});


}
onSubmitReg = (event)=>{

fetch('http://localhost:3000/register',{
  method:'post',
  headers:{'Content-Type': 'application/json'},
  body: JSON.stringify({
      name:this.state.nameReg,
      email:this.state.emailReg,
      password:this.state.PasswordReg



  })

})
.then(response => response.json())
.then(user => {

console.log(user);
if(user.id){



this.props.loadUser(user)
console.log(user);
this.props.onRouteChange('home');

}


})


}

render(){
return(
<article className="br3 ba dark-gray b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5 center">
<main className="pa4 black-80">
  <div className="measure ">
    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
      <legend className="f1 fw6 ph0 mh0">Register</legend>
      <div className="mt3">
        <label className="db fw6 lh-copy f6" htmlFor="name">Name</label>
        <input onChange={this.onRegisterName}
         className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="text" name="name"  id="name"/>

      </div>
      <div className="mt3">
        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
        <input onChange={this.onRegisterEmail} className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address"  id="email-address"/>
      </div>
      <div className="mv3">
        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
        <input onChange={this.onRegisterPassword} className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password"/>
      </div>
    </fieldset>
    <div className="">
      <input 
      onClick={this.onSubmitReg}
      className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
      type="submit" 
      value="Register"
      />
    </div>

  </div>
</main>
</article>

	);
}

}

export default Register ;
